con:configuration.scriptText:
//....check for response time
//log.info(messageExchange.getTimeTaken());
assert messageExchange.getTimeTaken()< 2000;


//....getting/check for Endpoint
//log.info(messageExchange.getEndpoint());
def endpoint = "http://webservices.oorsprong.org/websamples.countryinfo/CountryInfoService.wso";
assert messageExchange.getEndpoint()==endpoint;


//....check for headers
//log.info(messageExchange.responseHeaders["Web-Service"]);
def webServHeader = ["DataFlex 19.1"];
assert messageExchange.responseHeaders["Web-Service"] == webServHeader;

//....check for Attachments
//log.info(messageExchange.responseAttachments.length);
assert messageExchange.responseAttachments.length == 0;


//....Validate response nodes
def grvUtls = new com.eviware.soapui.support.GroovyUtils( context );
//def requestHolder = grvUtls.getXmlHolder(messageExchange.requestContent);
def responseHolder = grvUtls.getXmlHolder(messageExchange.responseContent);
def currency = responseHolder.getNodeValue("//m:CountryCurrencyResult/m:sName");
//log.info("  >>>  Country Currency: "+currency);
assert currency == "Rupees";


//....To get the XML response
def resp = messageExchange.responseContentAsXml.toString();
log.info(resp);

//...Get Json response
import groovy.json.JsonSlurper;
def respMessage = messageExchange.response.responseContent;
def json = new JsonSlurper().parseText(respMessage);
//log.info(json);


//....Assert node values
//log.info(json.name.toString());
//log.info(json.capital.toString());
assert json.name.common.toString() == "[Taiwan]";


//....To get the TestStep name
testStepName = messageExchange.modelItem.testStep.getName();
log.info(testStepName);


//....To store the respone as XML
//xmlHold = messageExchange.responseContentAsXml.toString();

con:soapui-project.con:afterLoadScript:
def su = new File("D:/SoapUI Projects/setup.txt").text;
def td = new File("D:/SoapUI Projects/teardown.txt").text;

project.getTestSuiteList().each{ testSuite ->
	testSuite.getTestCaseList().each{ testCase ->
		testCase.setSetupScript(su);
		testCase.setTearDownScript(td);
	}
}
